mapper.insert:
INSERT INTO MEMBER 
(
	NO,
	ID,
	PWD,
	NICK,
	GENDER,
	MBTI,
	FILE_NAME,
	ENROLL_DATE
	)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL
	,#{id}
	,#{pwd}
	,#{nick}
	,#{gender}
	,#{mbti}
	,#{fileName}
	,SYSDATE
)

mapper.update:
UPDATE MEMBER
	SET PWD = #{pwd}, NICK = #{nick}, GENDER = #{gender}, MBTI = #{mbti}, FILE_NAME = #{fileName}, MODIFY_DATE = SYSDATE
	WHERE NO = #{no}
